# --- Funzione di validazione input ---
def valida_squadra(nome_squadra):
    """
    Restituisce il nome corretto della squadra se esiste nel dataset,
    indipendentemente da maiuscole/minuscole.
    """
    tutte_squadre = {partita[1] for partita in li} | {partita[2] for partita in li}
    for squadra in tutte_squadre:
        if squadra.lower() == nome_squadra.lower():
            return squadra
    return None


